abi=json.loads(abi_string)
Contract = web3.eth.contract(address=contract_address, abi=abi)

''' WCFX address (eSpace mainnet)'''
wcfx_address = Web3.to_checksum_address("0x14b2d3bc65e74dae1030eafd8ac30c533c976a9b")

cache = []

if __name__ == '__main__':

    ''' Token to swap CFX for '''
    token_address = Web3.to_checksum_address("0xfb16f7405D26c5dA7fA41E6f373B57953a121fF2")

    ''' Amount of CFX to spend '''
    amount_to_buy_for = web3.to_wei(1, "ether")

    buy_path = [wcfx_address, token_address]
    slippage = 0.0025
    amountsOutExact = Contract.functions.getAmountsOut(amount_to_buy_for, buy_path).call()
    amountOutMin = int(amountsOutExact[1] * (1 - slippage))

    tx_params = {
        "chainId": web3.eth.chain_id,
        "nonce": start_nonce,
        "from": account.address,
        'gasPrice': web3.eth.gas_price,
        "gas": 1000000,
        "value": amount_to_buy_for
    }

    ''' swapExactETHForTokens
        You pay CFX and recieve `token_address`
        We will only do only one transaction
    '''
    tx_built = Contract.functions.swapExactETHForTokens(
        amountOutMin,
        buy_path,
        account.address,
        int(time.time())+180
    ).build_transaction(tx_params)

    # Estimate
    estimate_result = web3.eth.estimate_gas(tx_built)
    tx_built['gas'] = estimate_result

    # Sign
    tx_signed = account.sign_transaction(tx_built)
    cache.append(tx_signed)

    # Send and display status
    print("packed %s transactions" % (1) )
    start = time.time()
    print("Starting to send transactions @ %s " % (time.strftime("%Y-%m-%d %H:%M:%S", time.localtime(time.time()))) )
    res = [web3.eth.send_raw_transaction(j.rawTransaction) for j in cache]
    print("Sent %s transactions in %s seconds" % (1, time.time() - start) )
    print("From: %s" % (account.address) )
    print("Using Contract: %s" % (contract_address) )
    print("Gas estimate: %s" % (estimate_result) )
    print("Last Nonce hash: %s" % (res[-1].hex()) )
    print("Last Nonce #: %s" % (start_nonce))
    print("Awaiting Last Nonce transaction receipt...\n")
    print(web3.eth.wait_for_transaction_receipt(res[-1].hex()) )
